import timeit
   
def substrings_from_string(my_string):
    max_len = len(my_string)
    my_list = [my_string[i:j] for i in range(0, max_len) for j in range(1, max_len+1)]
    my_list_new = [my_string[i:j] for i in range(max_len) for j in range(i + 1, max_len + 1)]
    my_list_ok = [item for item in my_list if item != '']
    return my_list_new

def split_list(my_list_ok):
    # split the list in 2 lists: one with words beginning with a vowel, another with words begining with a consonant
    my_list_vowel = [item for item in my_list_ok if item[0] in ['A', 'E', 'I', 'O', 'U']]
    my_list_cons = [item for item in my_list_ok if item[0] not in ['A', 'E', 'I', 'O', 'U']]
    return my_list_vowel, my_list_cons
    


if __name__ == '__main__':
    
    starttime1 = timeit.default_timer()
    print("The start time 1 is :", starttime1)
    
    my_string = '111'
    #my_string = 'BANANA'
    #my_string = ''
    my_list_new = substrings_from_string(my_string)
    #print(my_list_ok)
    print(my_list_new)
    print('TOTAL = ', len(my_list_new))
    
    starttime2 = timeit.default_timer()
    print("The start time 2 is :", starttime2)

    result = split_list(my_list_new) #a tuple with both lists of sublists
    #print(result)
    
    v = len(result[0]) #number of substrings starting with a vowel
    c = len(result[1]) #number of substrings starting with a consonant
    #print(v, c)
    
    # The winner is:
    if c>v:
        print('Stuart', c)
    elif v>c:
        print('Kevin', v)
    else:
        print('Draw')
        
    print("The end time is :", timeit.default_timer())
    print("The time difference is :", timeit.default_timer() - starttime1)
    